    fun getMovies(): List<MoviesTvData> {
        val listMovies: ArrayList<MoviesTvData> = arrayListOf()
        return try {
            val json = getData()
            json?.let { it.movies.forEachIndexed { _, moviesData -> listMovies.add(moviesData) } }
            listMovies
        } catch (ioException: IOException) {
            ioException.printStackTrace()
            emptyList()
        }
    }

    fun getTvShows(): List<MoviesTvData> {
        val listShow: ArrayList<MoviesTvData> = arrayListOf()
        return try {
            val json = getData()
            json?.let { it.shows.forEachIndexed { _, moviesData -> listShow.add(moviesData) } }
            listShow
        } catch (ioException: IOException) {
            ioException.printStackTrace()
            emptyList()
        }
    }

    fun generateRemoteTvShowData(): ResponseClass {
        val tvShowsResponseList = ArrayList<MoviesTvDataResponse>()
        val tvShowsList = getTvShows()
        for (tvShow in tvShowsList) {
            tvShowsResponseList.add(MoviesTvDataResponse(tvShow.original_title,
                tvShow.poster_path,
                tvShow.overview,
                tvShow.vote_average,
                tvShow.vote_count,
                tvShow.original_language,
                tvShow.popularity))
        }
        return ResponseClass(tvShowsResponseList)
    }

    fun generateRemoteMovieData(): ResponseClass {
        val moviesResponseList = ArrayList<MoviesTvDataResponse>()
        val moviesList = getMovies()
        for (movie in moviesList) {
            moviesResponseList.add(MoviesTvDataResponse(movie.original_title,
                movie.poster_path,
                movie.overview,
                movie.vote_average,
                movie.vote_count,
                movie.original_language,
                movie.popularity))
        }
        return ResponseClass(moviesResponseList)
    }


    private fun getData(): ParseJson? {
        val gson = Gson()
        return gson.fromJson(jsonString, ParseJson::class.java)
    }

